//will announce players and start of game
farseer.on("game-start", function(data){
	console.log("GAME STARTED");
	data.forEach(function(player){
		console.log("Player:", player.name + "\n", "id:", player.id + "\n", "Team:", player.team + "\n");
	});
});

//will announce the winner
farseer.on("game-over",function(data){
	console.log("GAME OVER");
	data.forEach(function(player){
		if(player.status === "WON"){
			console.log("Player", player.name, "has won the game");
		}
	});
});

//will log all the important ids of an entity
farseer.on("zone-change", function(data){
	console.log("Card",data.cardName, "with an id of", data.entityId, "played by", data.fromTeam, "from zone", data.fromZone, "to zone", data.toZone );
});

farseer.start();